import {
    pokemonAddActions
} from "./storeOfSlices"

export const fetch20Pokemons = () => {
    return async dispatch => {

        const fetchData = async () => {
            const response = await fetch("https://pokeapi.co/api/v2/pokemon?limit=20")

            if (!response.ok) {
                throw new Error('failed fetch Pokemons')
            }

            const responsePokemons = await response.json()
            await console.log(responsePokemons)
            return responsePokemons
        }
        try {
            const data = await fetchData()
            await dispatch(pokemonAddActions.updatePokemonList(data))

        } catch (error) {
            console.log(error)

        }


    }
}
