import React from "react";
import { useSelector } from "react-redux";
import SinglePokemon from "./SinglePokemon";
import styles from "./PokemonList.module.css";

export default function PokemonList(props) {
  const pokemons = useSelector((state) => state.pokemonAddSlice.pokemonList);
  const offSet = useSelector((state) => state.pokemonAddSlice.offSet);
  const pokemonsTodisplayLi = pokemons.map((pokemon) => (
    <SinglePokemon
      key={pokemon.id}
      type={pokemon.types[0].type.name}
      name={pokemon.name}
      sprite={pokemon.sprites.front_default}
    >
      {" "}
    </SinglePokemon>
  ));
  return (
    <>
      {" "}
      <ul className={styles.list}> {pokemonsTodisplayLi} </ul>
      <button onClick={() => props.loadMore(offSet)}>load More</button>
    </>
  );
}
