import {
  useEffect,
  useCallback
} from "react";
import {
  useDispatch,
  useSelector
} from "react-redux";
import {
  fetch20Pokemons
} from "./components/store/pokemon-actions";
import styles from "./App.module.css";
import PokemonList from "./components/pokemonsList/PokemonList";
import LoadingPage from "./components/Ui/loadingPage";

function App() {
  const dispatch = useDispatch();
  const isFirstLoad = useSelector((state) => state.pokemonAddSlice.firstLoad);

  const loadPokemons = useCallback(
    (offset = 0) => {
      dispatch(fetch20Pokemons(offset));
    },
    [dispatch]
  );
  useEffect(() => {
    loadPokemons();
  }, [loadPokemons]);

  return ( <
    div className = {
      styles.wrapper
    } >
    <
    PokemonList loadMore = {
      loadPokemons
    } > < /PokemonList> {
      isLoading && < LoadingPage > < /LoadingPage>} <
        /div>
    );
  }

  export default App;
