import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetch20Pokemons } from "./components/store/pokemon-actions";
import "./App.css";
import PokemonList from "./components/pokemonsList/PokemonList";

function App() {
  const dispatch = useDispatch();
  const offset = useSelector((state) => state.pokemonAddSlice.offset);

  useEffect(() => {
    dispatch(fetch20Pokemons(offset));
  }, [dispatch]);

  return (
    <div>
      <button onClick={() => dispatch(fetch20Pokemons(offset))}>
        load next 20
      </button>
      <PokemonList></PokemonList>
    </div>
  );
}

export default App;
