import { useEffect, useCallback } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetch20Pokemons } from "./components/store/pokemon-actions";
import "./App.css";
import PokemonList from "./components/pokemonsList/PokemonList";

function App() {
  const dispatch = useDispatch();
  const offset = useSelector((state) => state.pokemonAddSlice.offSet);
  const loadPokemons = useCallback(
    (offset = 0) => {
      dispatch(fetch20Pokemons(offset));
    },
    [dispatch]
  );
  useEffect(() => {
    loadPokemons();
  }, [loadPokemons]);

  return (
    <div>
      <button onClick={() => dispatch(fetch20Pokemons(offset))}>
        load next 20
      </button>
      <PokemonList loadMore={loadPokemons}></PokemonList>
    </div>
  );
}

export default App;
