import React from "react";
import { useState } from "react";
import styles from "./SinglePokemon.module.css";
import PokemonPopUp from "../pokemonPopUp/pokemonPopUp";

export default function SinglePokemon(props) {
  const [modalState, setModalState] = useState(false);
  const openModalHandler = () => {
    setModalState((state) => !state);
  };
  return (
    <>
      <li onClick={openModalHandler} className={styles.pokeLi}>
        <div
          className={styles.img}
          style={{ backgroundImage: `url(${props.sprite})` }}
        ></div>
        <h3>{props.name}</h3>
        <div>{props.type}</div>
      </li>
      {modalState && (
        <PokemonPopUp {...props} closeModal={openModalHandler}></PokemonPopUp>
      )}
    </>
  );
}
