import {
    pokemonAddActions
} from "./storeOfSlices"

export const fetch20Pokemons = () => {
    return async dispatch => {

        const fetchPokemonList = async () => {
            const response = await fetch("https://pokeapi.co/api/v2/pokemon")

            if (!response.ok) {
                throw new Error('failed fetch Pokemons')
            }

            const responsePokemons = await response.json()
            await console.log(responsePokemons)
            return responsePokemons
        }
        try {
            const pokemonsToFetch = await fetchPokemonList()
            const {
                results: pokemons
            } = pokemonsToFetch
            console.log(pokemons)

            await dispatch(pokemonAddActions.updatePokemonList())

        } catch (error) {
            console.log(error)

        }


    }
}
