import {
    createSlice
} from "@reduxjs/toolkit";
export const pokemonAddSlice = createSlice({
    name: 'addSlice',
    initialState: {
        pokemonList: []
    },
    reducers: {
        updatePokemonList: (state, action) => {
            state.pokemonList = [...state.pokemonList, ...action.payload.pokemons]
            console.log(state.pokemonList)
        }
    }

})
