import React from "react";
import {
  useSelector
} from "react-redux";
import SinglePokemon from "./SinglePokemon";
import styles from "./PokemonList.module.css";
import Button from "../Ui/Button";

export default function PokemonList(props) {
  const pokemons = useSelector((state) => state.pokemonAddSlice.pokemonList);
  const offSet = useSelector((state) => state.pokemonAddSlice.offSet);
  const loadingFirst = useSelector((state) => state.pokemonAddSlice.isLoading);
  const pokemonsTodisplayLi = pokemons.map((pokemon) => ( <
    SinglePokemon key = {
      pokemon.id
    }
    type = {
      pokemon.types[0].type.name
    }
    name = {
      pokemon.name
    }
    sprite = {
      pokemon.sprites.front_default
    }
    weight = {
      pokemon.weight
    }
    height = {
      pokemon.height
    } >
    {
      " "
    } <
    /SinglePokemon>
  ));
  return ( <
    > {
      " "
    } <
    ul className = {
      styles.list
    } > {
      " "
    } {
      pokemonsTodisplayLi
    } {
      " "
    } {
      !loadingFirst && ( <
        Button message = "Load more..."
        onClick = {
          () => props.loadMore(offSet)
        }
        className = {
          styles.button
        } >
        < /Button>
      )
    } <
    /ul> <
    />
  );
}
